class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>diff(nums.size());
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            diff[i]=target[i]-nums[i];
        }
        long long ans=0;
        long long curr = 0;

        for (int i = 0; i < n; i++) {
            if(i==0)
            {
                int temp = abs(diff[i]);
                if (temp > 0) {
                    ans =ans+ temp;
                }
            }
            else if ((diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int temp = abs(diff[i]) - abs(curr);
                if (temp > 0) {
                    ans =ans+ temp;
                }
            } else {
                ans += abs(diff[i]);     
            }
            curr = diff[i];
        }
        return ans;
    }
};
