class Solution {
    using LL = long long;
public:
    LL minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        std::vector<LL> diff(n);

        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
            // std::cout << diff[i] << " ";
        }

        LL operations = 0;
        int minOp = 0;
        int i = 0;
        while (i < n) {
            if (diff[i] == 0) {
                ++i;
                continue;
            }

            if (diff[i] > 0) {
                int j = i + 1;
                while (j < n && diff[j - 1] >= diff[j] && diff[i] * diff[j] > 0) {
                    ++j;
                }
                if (j == n) {
                    operations += diff[i] - minOp;
                    i = j;
                } else if (diff[j - 1] < diff[j]) {
                    operations += diff[i] - minOp;
                    minOp = diff[j - 1];
                    i = j;
                } else {
                    operations += diff[i] - minOp;
                    minOp = 0;
                    i = j;
                }
            } else {
                int j = i + 1;
                while (j < n && diff[j - 1] <= diff[j] && diff[i] * diff[j] > 0) {
                    ++j;
                }
                if (j == n) {
                    operations += abs(diff[i]) - minOp;
                    i = j;
                } else if (diff[j - 1] > diff[j]) {
                    operations += abs(diff[i]) - minOp;
                    minOp = abs(diff[j - 1]);
                    i = j;
                } else {
                    operations += abs(diff[i]) - minOp;
                    minOp = 0;
                    i = j;
                }
            }

            // std::cout << i << "," << operations << "\n";
        }

        return operations;
    }
};

