class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> dif ;
        // = {5, 4, 3, 7, -1, 8};
        int n = nums.size();
        for(int i=0; i<n; i++) {
            dif.push_back(nums[i] - target[i]);
            cout<<dif[i]<<" ";
        }

        long long count = abs(dif[0]);
        bool posconsec = false, negconsec = false;

        for(int i=1; i<n; i++) {
            if(dif[i-1]>=0 && dif[i]>=0) {
                posconsec = true;
            }
            else if(dif[i-1]<0 && dif[i]<0) {
                negconsec = true;
            }
            else {
                posconsec = false;
                negconsec = false;
            }

            if(posconsec) {
                if(dif[i] > dif[i-1])
                    count += dif[i] - dif[i-1];
                continue;
            }
            if(negconsec) {
                if(dif[i] < dif[i-1])
                    count += abs(dif[i] - dif[i-1]);
                continue;
            }
                count += abs(dif[i]);
        }

        return count;
    }
};
