class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        long long sum = 0;
        for (int i = 0; i < nums.size(); i++) {
            if ((nums[i] + sum) == target[i])
                continue;
            else if ((nums[i] + sum) < target[i]) {
                int d = target[i] - (nums[i]+sum);
                if (sum < 0) {
                    if (d <= abs(sum))
                        sum = sum + d;

                    else {
                        d = d + sum;
                        sum = d;
                        ans += d;
                    }
                } else {

                    ans += (d);
                    sum += d;
                     if(i==0) cout<<sum;
                }
                // if(i==0) cout<<ans;

            } else {
                int d = (nums[i]+sum) - target[i];
                // if(i==2) cout<<d;
                if (sum > 0) {
                    if (d <= sum)
                        sum = sum - d;

                    else {
                        d = d - sum;
                        sum = -d;
                        ans += d;
                        // if(i==2) cout<<sum;
                     }
                }     
                else {
                        ans += (d);
                        sum -= d;
                }

            }
        }
        return ans;
    }
};
