class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        reqi = []
        reqd = []
        for i in range(len(nums)):
            if nums[i] > target[i]:
                reqd.append(nums[i] - target[i])
                reqi.append(0)
            else:
                reqd.append(0)
                reqi.append(target[i] - nums[i])


        ans = 0 
        for i in range(len(nums) - 1):
            if reqi[i] > reqi[i+1]:
                ans += reqi[i] - reqi[i+1]

        ans += reqi[-1] 
        for i in range(len(nums) - 1):
            if reqd[i] > reqd[i+1]:
                ans += reqd[i] - reqd[i+1]

        ans += reqd[-1] 
        return ans
