#include <vector>
#include <cmath>

using namespace std;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int size = nums.size();
        vector<int> difference(size);

        for (int i = 0; i < size; ++i) {
            difference[i] = target[i] - nums[i];
        }

        long long totalOperations = 0;
        long long currentSegmentDifference = 0;

        for (int i = 0; i < size; ++i) {
            if (i == 0 || (difference[i - 1] > 0 && difference[i] > 0) || (difference[i - 1] < 0 && difference[i] < 0)) {
                int additionalOperations = abs(difference[i]) - abs(currentSegmentDifference);
                if (additionalOperations > 0) {
                    totalOperations += additionalOperations;
                }
                currentSegmentDifference = difference[i];
            } else {
                totalOperations += abs(difference[i]);
                currentSegmentDifference = difference[i];
            }
        }

        return totalOperations;
    }
};
