class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int te = -1;
        long long ans = 0;
        int n = target.size();
        for (int i = 0; i < n; ++i) nums[i] -= target[i];
        nums.push_back(0), ++n;
        //for (int i = 0; i < n; ++i) cout << nums[i] << " ";
        int idx1, idx2;
        stack<int> s;
        for (int i = 0; i < n; ++i) {
            if (te == -1) {
                if (nums[i] == 0) continue;
                te = nums[i] > 0 ? 1 : 0;
                idx1 = i;
            } else if (nums[i] > 0 != te || nums[i] == 0) { 
                idx2 = i;
                // 跑一个单调栈
                for (int j = idx1; j < idx2; ++j) {
                    nums[j] = abs(nums[j]);
                    if (!s.empty() && nums[j] < s.top()) {
                        ans += s.top() - nums[j];
                        while (!s.empty() && nums[j] < s.top()) s.pop();
                    }
                    s.push(nums[j]);
                }
                ans += s.top(); 
                while (!s.empty()) s.pop();
                if (nums[i] == 0) te = -1;
                else te = nums[i] > 0 ? 1 : 0, idx1 = i;
            }
        }
        return ans;
    }
};
