class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [y-x for x, y in zip(nums, target)]
        #print(diffs)

        steps = 0
        prev = 0

        for x in diffs:
            if x > 0:
                if prev < 0:
                    prev = 0
                steps += max(0, x - prev)
            if x < 0:
                if prev > 0:
                    prev = 0
                steps += max(0, prev - x)
            prev = x
        return steps
