class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i]-nums[i] for i in range(len(nums))]
        moves = 0
        for i in range(len(nums)):
            k = i + 1
            tmp = diff[i]
            while(k < len(nums) and diff[i] * diff[k] > 0):
                if tmp > 0:
                    tmp = min(tmp, diff[k])
                else:
                    tmp = max(tmp,diff[k])
                diff[k] -= tmp
                k += 1
            moves += abs(diff[i])
        return moves
