class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0ll;
        long long currOps=0ll;
        for(int i=0;i<nums.size();i++)
        {
            long long diff=target[i]-nums[i];
            if(diff>=0 && currOps>=0)
            {
                if(currOps>=diff)
                {
                    currOps=diff;
                }
                else
                {
                    ans=ans+diff-currOps;
                    currOps=diff;
                }
            }
            else if(diff>=0 && currOps<0)
            {
                ans=ans+diff;
                currOps=diff;
            }
            else if(diff<=0 && currOps>=0)
            {
                ans=ans-diff;
                currOps=diff;
            }
            else
            {
                if(currOps<=diff)
                    currOps=diff;
                else
                {
                    ans=ans-diff+currOps;
                    currOps=diff;
                }
            }
        }
        return ans;
    }
};
