class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int n=nums.size();
        vector<int> v(n);

        for(int i=0;i<n;i++){
            v[i]=t[i]-nums[i];
        }

        long long ans=0;
        for(int i=0;i<n;i++){
            if(v[i]==0) continue;
            if(v[i]>0){
              int j=i+1;
              long long c=v[i];
               while(j<n && v[j]>0){
                  if(v[j]>v[j-1])
                  c+=v[j]-v[j-1];
                  j++;
               }
                ans+=c;
                i=j-1;    
            }
            else{
              int j=i+1;
              long long c=abs(v[i]);
               while(j<n && v[j]<0){
                  if(abs(v[j])>abs(v[j-1]))
                  c+=abs(v[j]-v[j-1]);
                  j++;
               }
                ans+=c;
                i=j-1; 
            }
        }
        return ans;
    }
};
