class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& tar) {
        int n = v.size();
        for(int i = 0;i < n;i++){
            v[i] = tar[i] - v[i];
        }

        long long ans = 0, tree = 0;
        ans = abs(v[0]);
        tree = v[0];

        for(int i = 1;i < n;i++){
            if(abs(tree) >= abs(v[i]) && ((v[i] <= 0 && tree <= 0) || v[i] >= 0 && tree >= 0)){
                tree = v[i];
                continue;
            }

            int first = abs(v[i]), second = abs(tree - v[i]);
            if(first <= second){
                tree = v[i];
            }
            else{
                tree = v[i];
            }

            // cout << i << " " << first << " " << second << endl;
            ans += min(first, second);
        }

        // for(auto &i: v){
        //     cout << i << " ";
        // }
        cout << endl;
        return ans;
    }
};
