class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long size = nums.size();
        vector<long long> delta(size);

        for (long long i = 0; i < size; ++i) 
            delta[i] = target[i] - nums[i];

        long long operations = abs(delta[0]);
        long long previousDelta = delta[0];

        for (long long i = 1; i < size; ++i) {
            if ((previousDelta < 0 && delta[i] < 0) || (previousDelta > 0 && delta[i] > 0)) {
                if (abs(previousDelta) < abs(delta[i])) {
                    operations += abs(delta[i]) - abs(previousDelta);
                }
            } else {
                operations += abs(delta[i]);
            }
            previousDelta = delta[i];
        }

        return operations;
    }
};
