class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0; i < n; i++){
            diff[i] = target[i] - nums[i];
        }

        long long cnt = 0;
        int sd = 0;
        for(int i = 0; i < n; i++){
            if(i == 0 || (diff[i-1] > 0 && diff[i] > 0) || (diff[i-1] < 0 && diff[i] < 0)){
                int ad = abs(diff[i]) - abs(sd);
                if(ad > 0){
                    cnt += ad;
                }
                sd = diff[i];
            }
            else{
                cnt += abs(diff[i]);
                sd = diff[i];
            }
        }
        return cnt;


