class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        ans = pre = 0
        tmp = [] # maintain current positive/negative streaks
        for i in range(len(nums)):
            diff = target[i] - nums[i]
            if diff == 0:
                pre = 0
            else:
                if diff*pre < 0:
                    ans += abs(diff)
                    pre = diff
                else:
                    ans += max(diff-pre,0) if diff>0 else max(pre-diff,0)
                    pre = diff

        return ans
