class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        int n = nums.length;
        int pre = 0;
        for (int i = 0; i < n; i++) {
            int cur = target[i] - nums[i];
            if (pre == 0) {
                pre = cur;
            } else if (pre == cur) {
                continue;
            } else if (cur == 0) {
                ans += Math.abs(pre);
                pre = 0;
            } else if (pre > 0 && cur > 0) {
                if (cur > pre) {
                    pre = cur;
                    continue;
                } else {
                    ans += pre - cur;
                    pre = cur;
                    continue;
                }
            } else if (pre < 0 && cur < 0) {
                if (cur < pre) {
                    pre = cur;
                    continue;
                } else {
                    ans += cur - pre;
                    pre = cur;
                    continue;
                }
            } else if (pre > 0 && cur < 0) {
                ans += pre;
                pre = cur;
            } else if (pre < 0 && cur > 0) {
                ans += -pre;
                pre = cur;
            }
        }
        return ans + Math.abs(pre);
    }
}
