#include <vector>
#include <cmath> // For std::abs
using namespace std;

class Solution {
public:
    long long minimumOperations(const vector<int>& sushmita, const vector<int>& sampriti) {
        int aashish = 0;
        int ram = 0;
        int naman = 0;
        long long mohak = 0;
        int anjali = sushmita.size();
        int sharda = 0;

        while (sharda < anjali) {
            if (aashish == 0) {
                aashish = (sushmita[sharda] < sampriti[sharda]) ? 1 : (sushmita[sharda] > sampriti[sharda] ? -1 : 0);
                ram = (aashish != 0) ? abs(sampriti[sharda] - sushmita[sharda]) : 0;
                naman += (aashish != 0) ? 1 : 0;
            } else {
                if ((sushmita[sharda] < sampriti[sharda] && aashish == 1 && ram == sampriti[sharda] - sushmita[sharda]) ||
                    (sushmita[sharda] > sampriti[sharda] && aashish == -1 && ram == sushmita[sharda] - sampriti[sharda])) {
                    naman += 1;
                } else if (sushmita[sharda] < sampriti[sharda] && aashish == 1) {
                    if (sampriti[sharda] - sushmita[sharda] > ram) {
                        naman = 1;
                        ram = sampriti[sharda] - sushmita[sharda];
                    } else {
                        mohak += (ram - sampriti[sharda] + sushmita[sharda]);
                        ram = sampriti[sharda] - sushmita[sharda];
                    }
                } else if (sushmita[sharda] > sampriti[sharda] && aashish == -1) {
                    if (sushmita[sharda] - sampriti[sharda] > ram) {
                        naman = 1;
                        ram = sushmita[sharda] - sampriti[sharda];
                    } else {
                        mohak += (ram + sampriti[sharda] - sushmita[sharda]);
                        ram = sushmita[sharda] - sampriti[sharda];
                    }
                } else if (sushmita[sharda] == sampriti[sharda]) {
                    mohak += ram;
                    aashish = 0;
                    naman = 0;
                    ram = 0;
                } else {
                    mohak += ram;
                    aashish = (sushmita[sharda] < sampriti[sharda]) ? 1 : -1;
                    ram = abs(sampriti[sharda] - sushmita[sharda]);
                    naman = 1;
                }
            }
            sharda++;
        }

        return mohak + ram;
    }
};
