#define ll long long
class Solution {
public:
    bool sameSign(ll int a, ll int b){
        if((a>0 && b>0) || (a<0 && b<0)){
            return 1;
        }
        return 0;
    }
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<int> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = b[i] - a[i];
        }
        ll int ans = 0;
        ll int cur = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || sameSign(diff[i],diff[i-1])) {
                ans += max(0ll, abs(diff[i]) - abs(cur));
                cur = diff[i];
            } else{
                ans += abs(diff[i]);
                cur = diff[i];
            }
        }
        return ans;
    }
};
