class Solution {

    long long fun(vector<long long> &a){
        if(a.size()==0) return 0ll;
        for(int i=0;i<a.size();i++) a[i] = abs(a[i]);
        long long count = a[0];
        for (int i = 1; i < a.size(); i++)
            count += max(a[i] - a[i - 1], 0ll);
        return count;
    }
public:
    long long minimumOperations(vector<int>& b, vector<int>& c) {
        int n = b.size();
        vector<int> a(n);
        for(int i=0;i<n;i++) a[i] = c[i] - b[i];

        vector<long long> tmp;
        long long ans = 0;
        for(int i=0;i<n;i++){
            if(tmp.size()==0){
                tmp.push_back(a[i]);
                continue;
            }
            if(a[i]==0){
                ans += fun(tmp);
                tmp.clear();
            }
            else if(a[i] > 0 and tmp.back() < 0){
                ans += fun(tmp);
                tmp.clear();
            }
            else if(a[i] < 0 and tmp.back() > 0){
                ans += fun(tmp);
                tmp.clear();
            }
            tmp.push_back(a[i]);
        }

        ans += fun(tmp);
        return ans;
    }
};
