class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int[] diff = new int[nums.length];
        for (int i = 0; i < nums.length; i++) {
            diff[i] = target[i] - nums[i];
        }
        // System.out.println(diff);

        int left = 0, peek = 0, bot = 0;
        long total = 0;
        int sign = 1;
        while (left < nums.length - 1) {
            if (diff[left] == 0) {
                left++;
                continue;
            }

            if (diff[left] > 0) sign = 1;
            else sign = -1;
            int cur = left + 1;
            while (cur < nums.length) {
                if (diff[cur]*sign > diff[cur - 1]*sign) {
                    cur++;
                } else {
                    break;
                }
            }

            peek = diff[cur - 1]*sign;
            total += peek - bot;
            // System.out.println(left + " " + cur + " " + peek);

            while (cur < nums.length) {
                if (sign == 1 && diff[cur] < 0) {
                    bot = 0;
                    break;
                } else if (sign == -1 && diff[cur] > 0) {
                    bot = 0; 
                    break;
                } else if (diff[cur]*sign > diff[cur - 1]*sign) {
                    bot = diff[cur - 1]*sign;
                    break;
                }
                cur++;
            }
            left = cur;
        }

        if (left == nums.length - 1) {
            if (diff[left] < 0) diff[left] = -diff[left];
            total += diff[left] - bot;
        }

        return total;
    }
}
