class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = [b - a for a, b in zip(nums, target)]
        res = 0
        p = 0
        for dd in d:
            if dd != p:
                if not (0 <= dd <= p or p <= dd <= 0):
                    res += min(abs(dd), abs(dd - p))
            p = dd
            #if dd != p:
            #    res += abs(dd - p)
            #    p = dd
        return res
