class Solution {
public:
    using ll = long long;
    long long minimumOperations(vector<int>& nums, vector<int>& t) {

        int n = nums.size();
        vector<ll> dff(n);

        for(int i = 0; i < n; i++){
            dff[i] = t[i] - nums[i];
        }
        ll ans = 0;
        int i = 0, prev_v = 0;

        // for(auto i: dff) cout<<i<<" ";
        // cout<<'\n';

        vector<ll> st;

        while(i <n){
            int res = dff[i];
            int j = i;

            while(j < n){
                if(res == dff[j]){}
                else break;

                j++;
            }

            st.push_back(res);

            i = j;

        }
        i = 0;

        for(auto i: st) cout<<i<<" ";

        cout<<'\n';

        bool fl = 0;

        int prev = 0;



        for(i = 0; i<st.size(); i++){
            if(i == 0){
                ans += abs(st[i]);
                if(st[i] >= 0) fl = 1;

                prev = st[i];

                continue;


            }

            if(st[i] >= 0 && fl){
                if(prev >= st[i]){
                    ans += 0;
                }
                else if(prev < st[i]){
                    ans += abs(st[i] - prev);
                }

                prev = st[i];

                fl = 1;
            }else if(st[i] >= 0 && fl == 0){
                ans += abs(st[i]);
                fl = 1;
                prev = st[i];
            }else if(st[i] < 0 && fl){
                ans += abs(st[i]);
                fl = 0;
                prev = st[i];
            }else if(st[i] < 0 && fl == 0){

                if(prev <= st[i] ) ans += 0;
                else if(prev > st[i]) {
                    ans += abs(st[i] - prev);
                }

                fl = 0;
                prev = st[i];

            }
        }

        return ans;


    }
};
