class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] d = new long[n];
        for(int i=0;i<n;i++){
            d[i] = target[i] - nums[i];
        }
        long tops = 0;
        long cd = 0;
        for(int i=0;i<n;i++){
            if(i==0 || (d[i-1]>0 && d[i]>0) || (d[i-1]<0 && d[i]<0)){
                long addops = Math.abs(d[i]) - Math.abs(cd);
                if(addops > 0){
                    tops += addops;
                }
                cd = d[i];
            }   else{
                tops += Math.abs(d[i]);
                cd = d[i];
            }
        }
        return tops;
    }
}
