class Solution {
public:
    struct Node {
        int value;
        int start;
        int end;
        Node(int v, int s, int e) {
            value = v;
            start = s;
            end = e;
        }
        const bool operator<(const Node& b) const {
            if (value != b.value) return value < b.value;
            if (start != b.start) return start < b.start;
            return end < b.end;
        }
    };
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int pre = 0;
        long long res = 0;
        for(int i = 0; i < nums.size(); i++) {
            int cur = max(0, nums[i] - target[i]);
            if (cur < pre) {
                res += pre - cur;
            }
            pre = cur;
        }
        res += pre;
        pre = 0;
        for(int i = 0; i < nums.size(); i++) {
            int cur = max(0, target[i] - nums[i]);
            if (cur < pre) {
                res += pre - cur;
            }
            pre = cur;
        }
        res += pre;
        return res;
    }
};
