import java.util.ArrayDeque;
import java.util.Deque;

class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n=nums.length;

        int[]diff=new int[n];
        for(int i=0;i<n;i++){
            diff[i]=nums[i]-target[i];
        }

        long ans=0;

        Deque<int[]>dqAdd=new ArrayDeque<>();
        Deque<int[]>dqSub=new ArrayDeque<>();

        int add=0;
        int sub=0;
        for(int i=0;i<n;i++){
            int x=diff[i];
            if(x>0){
                add=0;
                if(sub>=x){
                    sub=x;
                }else{
                    ans += x-sub;
                    sub=x;
                }
            }else{
                x=-x;
                sub=0;
                if(add>=x){
                    add=x;
                }else{
                    ans += x-add;
                    add=x;
                }
            }
        }

        return ans;
    }
}
