class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> s1(n + 1), s2(n + 1);
        for (int i = 0; i < n; i++) {
            s1[i] += nums[i]; s1[i + 1] -= nums[i];
            s2[i] += target[i]; s2[i + 1] -= target[i];
        }
        vector<long long> a(n);
        for (int i = 0; i < n; i++) {
            a[i] = s1[i] - s2[i];
        }
        long long ans = 0;
        long long add = 0, sub = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] == 0) continue;
            if (a[i] > 0) {
                if (a[i] >= sub) {
                    ans += a[i] - sub;
                    add += a[i] - sub;
                    sub = 0;
                } else {
                    sub -= a[i];
                }
            } else {
                a[i] = -a[i];
                if (a[i] >= add) {
                    ans += a[i] - add;
                    sub += a[i] - add;
                    add = 0;
                } else {
                    add -= a[i];
                }
            }
        }
        return ans;
    }
};
