class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        data=[a-b for a,b in zip(nums, target)]
        print(data)
        res=abs(data[0])
        for i in range(1, n):
            if data[i]*data[i-1]<0: res += abs(data[i])
            elif abs(data[i-1])<abs(data[i]): res+=abs(data[i] - data[i-1])

            # res += min(abs(data[i] - data[i-1]), abs(data[i]))
        return res
