class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = target.length;
        long res = 0;
        long freeVals = 0;
        for (int i = 0; i < n; i++) {
            int dif = target[i] - nums[i];
            if (freeVals >= dif && dif >= 0) {
                // it's free
            } else if (freeVals <= dif && dif <= 0) {
                // also free
            } else {
                // start new or reuse some
                res += Math.min(Math.abs(dif),
                               Math.abs(dif - freeVals));
            }

            freeVals = dif;
        }

        return res;
    }
}
