class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        for(int i=0;i<nums.size();i++){
            nums[i]-=target[i];
           // cout<<nums[i]<<endl;
        }
        ans=abs(nums[0]);
        for(int i=1;i<nums.size();i++){
            if(nums[i]>=0){
                if(nums[i-1]<0)ans+=nums[i];
                else if(nums[i]>nums[i-1])ans+=abs(nums[i]-nums[i-1]);
            }
            else{
                if(nums[i-1]>0)ans+=-nums[i];
                else if(nums[i]<nums[i-1])ans+=abs(nums[i]-nums[i-1]);
            }
            //cout<<ans<<endl;
            //ans+=min(abs(nums[i]),abs(nums[i]-nums[i-1]));
        }
        return ans;
    }
};
