class Solution {
public:
    #define ll long long
    ll f(vector<ll>&A)  
    {  
        ll ans = A[0], n=A.size();  
        for (int i = 1; i < A.size(); i++)ans += max(A[i] - A[i - 1], 0LL);
        return ans;  
    }  
    long long minimumOperations(vector<int>&a, vector<int>&t) {
        vector<vector<int>>v;
        vector<int>tmp;
        ll n=a.size(),ans=0;
        for(int i=0;i<n;i++){
            if(tmp.empty()){
                if(a[i]!=t[i])tmp.push_back(t[i]-a[i]);
            }
            else if(a[i]==t[i]){
                v.push_back(tmp);
                tmp.clear();
            }
            else{
                if(tmp.back()>0){
                    if(t[i]-a[i]>0)tmp.push_back(t[i]-a[i]);
                    else {
                        v.push_back(tmp);
                        tmp.clear();
                        tmp.push_back(t[i]-a[i]);
                    }
                }
                else{
                    if(t[i]-a[i]<0)tmp.push_back(t[i]-a[i]);
                    else {
                        v.push_back(tmp);
                        tmp.clear();
                        tmp.push_back(t[i]-a[i]);
                    }
                }
            }
        }
        v.push_back(tmp);
        for(auto val : v){
            vector<ll>tmp;
            for(auto e : val)tmp.push_back(abs(e));
            if(!tmp.empty())ans+=f(tmp);
        }
        return ans;
    }
};
