class Solution {
public:
    long long sub(vector<int> a){
        long long r = a[0], n = a.size(), i;
        for(i = 1; i < a.size(); ++i) r += max(a[i]-a[i-1], 0);
        return r;
    }

    int hao(int x){
        if (x >= 0) return 1;
        else return -1;
    }

    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long res = 0, i = 0, j, n = a.size();
        while (i < n){
            vector<int> c;
            j = i;
            while (j < n && hao(b[i] - a[i]) == hao(b[j] - a[j])){
                c.push_back((int)labs(b[j] - a[j]));
                ++j;
            }

            res += sub(c);
            i = j;
        }

        return res;
    }
};
