using i64 = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n = nums.size();
        for (int i = 0; i < n; i++){
            nums[i] -= target[i];
        }
        //change all numbers to zero
        i64 res = 0, prev = 0;

        for (auto v : nums){
            if (prev == 0){
                res += llabs(v);

            }
            else if (prev < 0){
                if (v <= prev){
                    res += prev - v;

                }
                else {
                    if (v > 0) res += v;
                }
            }
            else {
                if (v >= prev){
                    res += v - prev;
                }
                else {
                    if (v < 0) res += llabs(v);
                }

            }


            prev = v;
        }

        return res;

    }
};
