class Solution {
public:
  long long minimumOperations(vector<int>& A, vector<int>& B) {
    int64_t ans = 0;
    int n = A.size();
    for (int i = n-1; i>0; i--) A[i] -= A[i-1], B[i] -= B[i-1];

    int64_t ans1=0; 
    int64_t ans2=0; 
    for (int i = 0; i < n; i++) {
      int64_t a = A[i], b = B[i];
      if (a>b) ans1+=a-b;
      else ans2+=b-a;
    }
    return max(ans1, ans2);
  }
};
