class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        for (int i = 0; i < n; i++)
            nums[i] -= target[i], ans += abs(nums[i]);
        for (int i = 1; i < n; i++) {
            if (nums[i] > 0 && nums[i - 1] > 0)
                ans -= min(nums[i - 1], nums[i]);
            else if (nums[i - 1] < 0 && nums[i] < 0)
                ans += max(nums[i - 1], nums[i]);
        }
        return ans;
    }
};
