const int N=1e5+10;
int op[N];
int f[N];
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
    memset(op,0,sizeof op);
    memset(f,0,sizeof f);
    for(int i=0;i<nums.size();i++)
    {
        op[i]=target[i]-nums[i];
    }
    f[0]=op[0];
    for(int i=1;i<nums.size();i++)
    {
        f[i]=op[i]-op[i-1];
    }
    long long res1=0,res2=0;
    for(int i=0;i<nums.size();i++)
    {
        if(f[i]>0)
        res1+=f[i];
        if(f[i]<0)
        res2-=f[i];
    }
    return max(res1,res2);
    }
};
