class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        long long prevInc=0,prevDec=0;
        int n=nums.size() ;

        for(int i=0;i<n;i++) {
            if(nums[i]<target[i]) {
                long long diff= target[i]-nums[i];

                if(diff > prevInc){
                    ans += diff-prevInc;
                }
                prevInc=diff;
                prevDec=0;
            }
            else{
              long long diff = nums[i] - target[i];

              if(prevDec < diff) {
                ans += diff - prevDec ;
              }
              prevDec = diff;
              prevInc=0;
            }
        }

      return ans;
    }
};
