class Solution {
    int sign(int x){return x<0?-1:(x==0?0:1);}
public:
    long long minimumOperations(vector<int>& p, vector<int>& t) {
        int n = p.size();
        vector<int> d(n+2);
        for(int i = 0; i<n; i++) d[i+1] = p[i]-t[i];
        long long ans = 0;
        for(int i = 0; i<=n; i++) ans+=abs(d[i]-d[i+1]);
        return ans/2;
    }
};
