class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long count = 0;
        int neg=0;
        int pos=0;
        int n = nums.length;
        for(int i=0;i<n;i++){
            int diff = nums[i]-target[i];
            if(diff==0){
                neg=0;
                pos=0;
            } else if(diff<0){
                pos = 0;
                int reqNeg = Math.max(0,Math.abs(diff)-neg);
                count+=reqNeg;
                neg = Math.abs(diff);
            } else {
                neg = 0;
                int reqPos = Math.max(0,diff-pos);
                count+=reqPos;
                pos = diff;
            }
        }
        return count;

    }
}
