function minimumOperations(a: number[], b: number[]): number {
  const l = a.length;
  a = a.map((v, i) => b[i] - v);

  let r = 0;

  for (let i = 0; i < l - 1; ++i) {
    const av = a[i];
    const bv = a[i + 1];

    if (av === 0) {
      // ...
    } else if (bv === 0) {
      r += Math.abs(av);
    } else if ((av < 0 && bv < 0) || (av > 0 && bv > 0)) {
      if (Math.abs(av) <= Math.abs(bv)) {
        // ...
      } else {
        r += Math.abs(av) - Math.abs(bv);
      }
    } else {
      r += Math.abs(av);
    }
  }

  r += Math.abs(a[l - 1]);

  return r;
}
