class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        def solve(ar):
            if not ar:
                return 0
            st = []
            res = 0
            for val in ar:
                val = abs(val)
                if not st:
                    res += val
                elif st[-1] < val:
                    res += val - st[-1]
                while st and st[-1] >= val:
                    st.pop()
                st.append(val)
            return res                

        n = len(nums)
        res = 0
        start = 0
        while start < n:
            delta = target[start] - nums[start]
            if not delta:
                start += 1
                continue
            ar = [delta]
            end = start + 1
            while end < n:
                val = target[end] - nums[end]
                if delta * val <= 0:
                    break
                ar.append(val)
                end += 1
            res += solve(ar)
            start = end
        return res
