using ll = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int ns = nums.size();
        ll res = abs(nums[0]-target[0]);
        for(int i=1;i<ns;i++){
            int cur = nums[i]-target[i];
            int prev = nums[i-1]-target[i-1];
            if (ll(cur) * prev<0){
                res += abs(cur);
            }
            else{
                res += max(abs(cur)- abs(prev), 0);
            }
        }
        return res;
    }
};
