class Solution:
    def minimumOperations(self, A: List[int], B: List[int]) -> int:

        def cal(arr):
            if arr == []:
                return 0
            if arr[0] < 0:
                arr = [-a for a in arr]
            arr = [0] + arr
            res = 0
            for x, y in zip(arr[1:], arr):
                if x - y > 0:
                    res += x - y
            return res

        A = [a-b for a,b in zip(A, B)]
        A.append(0)
        cur = []
        res = 0
        for i, a in enumerate(A):
            if a == 0:
                res += cal(cur)
                cur = []
            elif cur == []:
                cur.append(a)
            elif a * cur[-1] > 0:
                cur.append(a)
            else:
                res += cal(cur)
                cur = [a]
        return res
