class Solution {
public:

    long long solve(vector<int>& vals) {
        if(vals.empty()) return 0ll;
        for(auto& x: vals) x = abs(x);
        int n = (int)vals.size();

        long long ret = vals[0];
        for(int i = 0; i < (int)vals.size() - 1; i++) {
            ret += max(0, vals[i + 1] - vals[i]);
        }
        return ret;

    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = (int)nums.size();
        for(int i = 0; i < n; i++) {
            nums[i] = nums[i] - target[i];
        }
        vector<int> vals;
        int cnt = 0;
        long long ret = 0;
        for(int i = 0; i < n; i++) {
            if(cnt%2) {
                if(nums[i] > 0) vals.push_back(nums[i]);
                else {
                    ret += solve(vals);
                    vals.clear();
                    cnt++;
                    if(nums[i] != 0) i--;
                }
            }
            else {
                if(nums[i] < 0) vals.push_back(nums[i]);
                else {
                    ret += solve(vals);
                    vals.clear();
                    cnt++;
                    if(nums[i] != 0) i--;

                }
            }
        }
        if(!vals.empty()) ret += solve(vals);
        return ret;
    }
};
