class Solution {
public:

    long long minimumOperations(vector<int>& s, vector<int>& t) {
        int n = t.size();
        vector<int> d(n);
        for(int i=0;i<n;i++){
            d[i] = t[i]-s[i];
        }
        long long ans = 0;
        long long cnt = 0;
        for(int i=0;i<n;i++){
            // cout<<d[i]<<" "<<cnt<<endl;

            if(d[i]>0){
                if(cnt<0){
                    cnt=0;
                }
                if(d[i]>cnt){
                    ans+=d[i]-cnt;
                    cnt=d[i];
                }else{
                    cnt=d[i];
                }
            }else if(d[i]<0){
                if(cnt>0){
                    cnt=0;
                }
                if(d[i]<cnt){
                    ans+=cnt-d[i];
                    cnt=d[i];
                }else{
                    cnt=d[i];
                }
                // ans+=-d[i];   
            }else{
                cnt=0;
            }
        }
        return ans;

    }
};
