#define ll long long

class Solution {
public:
    long long minimumOperations(vector<int>& A, vector<int>& T) {
        ll ans = 0;

        // same as usaco cownditioning
        for (int i = 0; i < A.size(); i++){
            ll v1 = A[i] - T[i];
            ll v2 = i + 1 < A.size() ? (A[i + 1] - T[i + 1]) : 0;
            ans += abs(v1 - v2);
            // cout << abs(v1 - v2) << endl;
        }
        ans += abs(A[0] - T[0]);
        return ans / 2;
    }
};
