class Solution {

    public long minimumOperations(int[] nums, int[] target) {
        int prev=0,pos=-1;
        long ans=0l;
        for(int i=0;i<nums.length;i++){
            int t=target[i]-nums[i];
            if(t>0){
               if(pos==0){
                if(prev<t){
                    ans +=1l*(t-prev);
                }
                    prev=t;
               }else{
                  pos=0;
                  ans +=1l*t;
                  prev=t;
               }
            }else if(t==0){
               pos=-1;
            }else{
               if(pos==1){
                if(prev>t){
                    ans +=-1l*(t-prev);
                }
                    prev=t;
               }else{
                  pos=1;
                  ans +=-1l*t;
                  prev=t;
               }
            }
        }
        return ans;
    }
}
