class Solution {
public:
    int a[100005];
    int b[100005];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        for(int i = 1;i <= n;i ++ ) {
            a[i] = nums[i - 1];
            b[i] = target[i - 1];
        }
        for(int i = 1;i <= n;i ++ ) {
            a[i] -= b[i];
        }
        for(int i = 1;i <= n;i ++ ) {
            b[i] = a[i] - a[i - 1];
        }
        long long cnt1 = 0, cnt2 = 0;
        for(int i = 1;i <= n;i ++ ) {
            if(b[i] < 0) {
                cnt1 += std::abs(b[i]);
            }
            if(b[i] > 0) {
                cnt2 += std::abs(b[i]);
            }
        }
        return std::max(cnt1, cnt2);
        // 要求全0
    }
};
