class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size(),i,j;
        vector<int> delta(n);
        for(i=0;i<n;i++){
            delta[i] = target[i]-nums[i];
        }

        int prev=0;
        long long res=0;

        for(int &d:delta){
            if(d>0){
                if(prev<0)prev=0;
                res+=max(0, d-prev);
            }
            else if(d<0){
                if(prev>0)prev=0;
                res+=max(0, prev-d);
            }
            prev=d;
        }
        return res;

    }
};
