class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        tmp = []
        for x, y in zip(nums, target):
            tmp.append(x - y)
        a = []
        cur = [tmp[0]]
        for i in range(1, n):
            if tmp[i] >= 0 and cur[-1] >= 0:
                cur.append(tmp[i])
            if tmp[i] < 0 and cur[-1] < 0:
                cur.append(tmp[i])
            if tmp[i] >= 0 and cur[-1] < 0:
                a.append(cur)
                cur = [tmp[i]]
            if tmp[i] < 0 and cur[-1] >= 0:
                a.append(cur)
                cur = [tmp[i]]
        a.append(cur)
        # print(a)

        def solve(b):
            cur = 0
            sz = len(b)
            for i in range(sz):
                b[i] = abs(b[i])

            res = b[0]
            for i in range(1, sz):
                if b[i] > b[i - 1]:
                    res += b[i] - b[i - 1]
            return res

        ans = 0
        for ls in a:
            ans += solve(ls)
        return ans
