impl Solution {
pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
    let mut result = 0;
    let mut change: i32 = 0;
    for v in nums.into_iter().zip(target).map(|(n , t)| n - t){
        if change.signum() * v.signum() == 1{
            if change.abs() <= v.abs(){
                result += (v.abs() - change.abs()) as i64;
            }

            change = v;
            continue;
        }

        change = v;
        result += v.abs() as i64;
    }

    result
}
}
